mapper.update:
update works_score
set score_detail = #{details}
where id = #{id}

select.where:
w.match_id = #{matchId}
and ws.type = #{type}
and ws.user_id = #{userId}
 and w.name like concat('%', #{workName}, '%')

mapper.select:
select count(*)
from works_score as ws
where ws.work_id = #{id}
  and ws.type = #{type}


where w.match_id = #{matchId}
and ws.type = #{type}
order by ws.work_id
